import React, { Component } from "react";

// Import STORE RESOURCES
import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "../store";

// Custom Components
import Header from "../Components/header";

class SignIn extends Component {
	render() {
		console.warn("check state", this.state);
		return (
		<React.Fragment>
			<Header {...this.props}/>
			<div className="container signin align-content-center" style={{marginTop:"100px"}}>
				<form onSubmit={e => e.preventDefault()}>
					<h3>Input your valid mail and password</h3>
					<div className="signin-input">
						<label for="email">Input your Email address</label><br/>
						<input 
							type="text"  
							name="email" 
							placeholder="example@alterra.id" 
							onChange={e => this.props.updateInput(e)} 
						/>
					</div>
					<div>
					<label for="password">Input your password</label><br/>
						<input 
							type="password"  
							name="password" 
							placeholder="*******" 
							onChange={e => this.props.updateInput(e)} 
						/>
					</div>
					<button style={{border:"1px solid #ececec", borderRadius:"20%"}} onClick={() => this.props.postLogin()}>LOGIN</button>
				</form>
			</div>
		</React.Fragment>
		)
	}
}

export default connect(
	"email, password",
	actions
)(withRouter(SignIn));
