import React from "react";
import axios from "axios";
import { connect } from "unistore/react";
import { actions } from "../store";

// import component
import Header from "../components/header";
import MovieDetail from "../components/MovieDetail";

class MoviesCategory extends React.Component {
  componentDidUpdate = (prevProps, prevState) => {
    if (prevProps.match.params.category !== this.props.match.params.category) {
      this.getDatabyCategory();
    }
  };

  async getData() {
    const self = this;
    // get 5 articles and assign them into state.listNews
    await axios.get(this.props.baseUrl).then(function(response) {
      console.log(response);
      self.props.setMovies(response.data.movies);
    });
  }

  async getDatabyCategory() {
    let filtered = this.props.listMovies
      .map(val => {
        if (val.Category === this.props.match.params.category) {
          return val;
        }
      })
      .filter(item => {
        return item !== undefined;
      });
    // console.log("ini param", this.props.match.params.category);
    console.log("ini category", filtered);

    await this.props.setMoviesByCategory(filtered);
    console.log("ini props", this.props);
  }

  async componentDidMount() {
    await this.getData();
    await this.getDatabyCategory();
  }

  render() {
    return (
      <div>
        <Header />
        <div className="container">
          <br />
          <div className="row justify-content-center mt-2">
            <div className="col-12">
              <MovieDetail data={this.props.listMoviesByCategory} />
            </div>
          </div>
        </div>
      </div>
    );
  }
}
// this.props.

// export default NewsByCategory;
export default connect(
  "listMovies,listMoviesCategory, baseUrl",
  actions
)(MoviesCategory);
