const list_article = [{number:"#1",title:this.props.title},
                  {number:"#2",title:this.props.title},
                  {number:"#3",title:this.props.title},
                  {number:"#4",title:this.props.title},
                  {number:"#5",title:this.props.title}]

class ListNews extends Component {
  render() {
    return (
        <div className="top-article">
          <div className="row top-article-h">
            <div className="col-8 top-article-head">
              <span>BERITA TERKINI</span>
            </div>
            <div className="col-4 top-article-head">
              <a href="#">lihat semua</a>
            </div>
          </div>
          <div className="row">
            <div className="col-12">
              <ul className="top-article-list list-unstyled">
                {list_article.map(data => (
                    <li className="top-article-list-map">
                        <p>{data.number}</p>
                        <a href="#">{data.title}</a>
                    </li>
                  )
                )}
              </ul>
            </div>
          </div>
        </div>
    );
  }
}

