import React from "react";
import axios from "axios";

import { connect } from "unistore/react";
import { actions } from "../store";

// import component
import Header from "../Components/header";
import MovieDetail from "../Components/movieDetail";

class MoviesCategory extends React.Component {
    handleRouterCategoryMovies = async categoryName => {
		const category = categoryName;
		await this.props.history.replace("/movies-category/" + category);
		await this.props.setCategory(this.props.match.params.category);
		console.log(this.props.paramsCategory)
		this.props.moviesCategory()
	}

	async getData() {
        const self = this;
        // get 5 articles and assign them into state.listNews
        await axios.get(this.props.baseUrl).then(function(response) {
          console.log(response);
          self.props.setMovies(response.data.movies);
        });
    }

    async getDatabyCategory() {
        let filtered = this.props.listMovies.map(val => {
            if (val.Category === this.props.match.params.category) {
              return val;
            }
          })
          .filter(item => {
            return item !== undefined;
          });
        // console.log("param", this.props.match.params.category);
        console.log("ini category", filtered);

        await this.props.setMoviesByCategory(filtered);
        console.log("ini props", this.props);
    }

    componentDidMount() {
        this.getData();
        this.getDatabyCategory();
    }

    render() {
    return (
      <div>
        <Header />
        <div className="container">
          <br />
          <div className="row justify-content-center mt-2">
            <div className="col-12">
              <MovieDetail data={this.props.listMoviesByCategory} />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

// export default MoviesByCategory;
export default connect(
  "listMovies, listMoviesCategory, baseUrl, paramsCategory",
  actions
)(MoviesCategory);
