import React, { Component } from "react";
import axios from "axios";

// Import STORE RESOURCES
import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import { actions, store } from "../store";

// Custom Components
import Header from "../Components/header";
import ListNews from "../Components/listNews";
import TopNews from "../Components/topNews";

class Home extends Component {
	componentDidMount = () => {
		this.topArticle();
		this.everything()
	};

	everything = () => {
		const urlEverything = this.props.baseUrl + "everything?domains=wsj.com,nytimes.com&apiKey=" + this.props.apiKey;
		const self = store;
		axios
		.get(urlEverything)
		.then(function(response) {
		  self.setState({ listEverything: response.data.articles, isLoading: false });
		  // handle success
		  console.log(response.data);
		})
		.catch(function(error) {
		  self.setState({ isLoading: false });
		  // handle error
		  console.log(error);
		});
	}

	handleInputChange = async event => {
		let value = event.target.value;
		console.warn("value check", value);
		await store.setState({ search: value });
		this.searchNews(value);
	};

	searchNews = async keyword => {
		const self = store;
		if (keyword.length > 3) {
			try {
				const response = await axios.get(
					baseUrl + "everything?q=" + keyword + "&apiKey=" + apiKey
				);
				self.setState({ listTopNews: response.data.articles });
			}	catch (error) {
				console.error(error);
			}
		}
	};

	topArticle = () => {
		const urlHeadline = this.props.baseUrl + "top-headlines?country=us&pageSize=5&apiKey=" + this.props.apiKey;
		const self = store;
		axios
		.get(urlHeadline)
		.then(function(response) {
		  self.setState({ listTopNews: response.data.articles, isLoading: false });
		  // handle success
		  console.log(response.data);
		})
		.catch(function(error) {
		  self.setState({ isLoading: false });
		  // handle error
		  console.log(error);
		});
	}

	render() {		
		const topHeadlines = this.props.listTopNews.filter(item => {
			if (item.content !== null && item.image !== null) {
				return item;
			}
			return false;
		})

		const headlineNews = topHeadlines.map((item, key) => {
			return (
				<TopNews
					key={key}
					title={item.title}
					img={item.urlToImage}
					content={item.description}
					url={item.url}
					time={item.publishedAt}
				/>
			);
		});

		const topEvery = this.props.listEverything.filter(item => {
			if (item.content !== null && item.image !== null) {
				return item;
			}
			return false;
		})

	return (
		<div>
			<Header 
			doSearch={event => this.handleInputChange(event)}
			{...this.props}
			/>
			<div className="container" style={{marginTop:"70px"}}>
				<div className="row ">
					<div className="col-5">
						<ListNews req_article={topEvery} />
					</div>
					<div className="col-7">
						{this.props.isLoading ? <div style={{ textAlign: "center" }}>Loading...</div> : headlineNews}               
					</div>
				</div>
			</div>
		</div>
	  );
	}
}

export default connect(
	"listTopNews, listEverything, isLoading, apiKey, baseUrl"
	)(withRouter(Home));
