              <a href="#">lihat semua</a>
            </div>
          </div>
          <div className="row">
            <div className="col-12">
              <ul className="top-article-list list-unstyled">
                {list_everything.map((data,key) => {
                  <li className="top-article-list-map">
                    <p>#{key}</p>
                    <a href="#">{data.title}</a>
                 </li>
                })}      
              </ul>
            </div>
          </div>
        </div>
    );
  }
}

