import React from "react";
import axios from "axios";
import Header from "../Components/header";
import { connect } from "unistore/react";
import { actions } from "../store";

class Login extends React.Component {
  updateInput = e => {
		this.setState({ [e.target.name]: e.target.value});
		console.warn("check target", e.target.value);
  };
  postLogin = () => {
		const data = {
			username: this.props.name,
			password: this.props.password
		};
		const self = this;
		axios
			.post(this.props.hostLogin, data)
			.then(async function(response) {
				console.log(response.data);
        await localStorage.setItem("username", response.data.user_data.username);
        localStorage.setItem("login_status", true);
        localStorage.setItem("email", response.data.user_data.email);
        localStorage.setItem("avatar", response.data.user_data.avatar);
        self.props.history.push("/profile");
			})
			.catch(function (error) {
				console.log(error);
			});
  };

  render() {
		console.warn("check state", this.state);
		return (
		<React.Fragment>
			<Header {...this.props}/>
			<div className="container signin align-content-center" style={{marginTop:"100px"}}>
				<form onSubmit={e => e.preventDefault()}>
					<h3>Input your username</h3>
					<div className="signin-input">
						<label for="name">Input your Username</label><br/>
						<input 
							type="text"  
							name="name" 
							onChange={e => this.updateInput(e)} 
						/>
					</div>
					<div>
					<label for="password">Input your password</label><br/>
						<input 
							type="password"  
							name="password" 
							placeholder="*******" 
							onChange={e => this.updateInput(e)} 
						/>
					</div>
					<button style={{border:"1px solid #ececec", borderRadius:"20%"}} onClick={() => this.postLogin()}>LOGIN</button>
				</form>
			</div>
		</React.Fragment>
		)
	}
}

// export default Login;
export default connect(
  "name, email, login_status, pasword",
  actions
)(Login);
