import createStore from "unistore";

// Initiate new store state and export it
const initialState = {
  name: "",
  email: "",
  avatar: "",
  login_status: null,
  listMovies: [],
  listMoviesByCategory: [],
  hostLogin: "https://api-todofancy.herokuapp.com/api/auth"
};

export const store = createStore(initialState);

// initiate actions function and export it
export const actions = store => ({
  setName(state, inputName) {
    return { name: inputName };
  },
  setAvatar(state, inputName) {
    return { avatar: inputName };
  },
  setEmail(state, nameEmail) {
    return { email: nameEmail };
  },
  login(state) {
    console.log(state.login_status);
    return { login_status: 1 };
  },
  logout(state) {
    console.log(state.login_status);
    return { login_status: null };
  },
  setMovies(state, listMovies) {
    return { listMovies: listMovies };
  },
  setMoviesByCategory(state, listMovies) {
    return { listMoviesByCategory: listMovies };
  }
});
