import React, { Component } from 'react';
import "../style/style.css";
import "../style/bootstrap.min.css";

class ListNews extends Component {
  render() {
    const topEvery = listEverything.filter(item => {
			if (item.content !== null && item.image !== null) {
				return item;
			}
			return false;
		})

		const everyNews = topEvery.map((item, key) => {
			return (
				<ListNews
					keyList={key}
					titleList={item.title}
					imgList={item.urlToImage}
					contentList={item.description}
					urlList={item.url}
					timeList={item.publishedAt}
				/>
			);
    });

    return (
        <div className="top-article">
          <div className="row top-article-h">
            <div className="col-8 top-article-head">
              <span>BERITA TERKINI</span>
            </div>
            <div className="col-4 top-article-head">
              <a href="#">lihat semua</a>
            </div>
          </div>
          <div className="row">
            <div className="col-12">
              <ul className="top-article-list list-unstyled">
                    <li className="top-article-list-map">
                        <p>#{this.props.keyList}</p>
                        <a href="#">{this.props.titleList}</a>
                    </li>
              </ul>
            </div>
          </div>
        </div>
    );
  }
}

